import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";
import Skeleton from "@material-ui/lab/Skeleton";
import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { gql, useQuery } from "@apollo/client";
import { layChinhTinhs } from "../lib/laSoAdapter";
const query = gql`
  query {
    allChinhTinhs: allTinhs {
      id
      name
    }
  }
`;
export default function chinhTinhs({ cung, autocomplete, onChange }) {
  const [CT, setCT] = useState({});
  const { data, loading, error } = useQuery(query);

  const shouldRedirect = !(loading || data);
  const router = useRouter();
  useEffect(() => {
    if (shouldRedirect) {
      router.push("/signin");
    }
    if (data?.allChinhTinhs && cung && autocomplete) {
      setCT(layChinhTinhs({ allChinhTinhs: data.allChinhTinhs, cung }).CT);
    }
  }, [shouldRedirect]);

  return data?.allChinhTinhs && !loading ? (
    <Autocomplete
      value={CT || {}}
      onChange={(event, value) => onChange(value)}
      multiple
      size="small"
      options={data.allChinhTinhs}
      getOptionLabel={(option) => {
        if (option) return option.name;
      }}
      filterSelectedOptions
      renderInput={(params) => (
        <TextField {...params} variant="outlined" label="ChÃ­nh Tinh" />
      )}
    />
  ) : (
    <Skeleton variant="rect" height={40} />
  );
}
