import { useRouter } from "next/router";
import { List as Products } from "../components/Product/list";

import { toAttributeGourpWhereInput } from "../lib/chip";
import { useState, useEffect, Fragment } from "react";
import { Container, Row, Col } from "reactstrap";
import { Sidebar } from "../components/Sidebar";
import { theme } from "../config/index";

import { Filter } from "../components/Product/filter";
import { List as Banners } from "../components/Banner/list";
import { css } from "../components/src/css";
import { IntroBox } from "../components/IntroBox";
const Index = () => {
  const router = useRouter();
  let query = router.query;
  const category = query.category;
  const categories = query.categories;
  const brand = query.brand;
  const search = query.search;
  const price_from = query.price_from ? Number(query.price_from) : 0;
  const price_to = query.price_to ? Number(query.price_to) : 999999999;
  const attributes = toAttributeGourpWhereInput(
    query.attributes ? query.attributes.split(",") : []
  );

  return (
    <Fragment>
      <Container fluid style={{ marginTop: theme.spacing(3) }}>
        <Banners style={{ height: "60vh" }} />
      </Container>
      <Container fluid>
        <Row noGutters>
          <Col xs={4} md={3} lg={3} xl={2}>
            <Sidebar
              onSearch={(search) => {
                setSearch(search);
              }}
            />
          </Col>
          <Col xs={8} md={9} lg={9} xl={10}>
            <div>
              {category ||
              categories ||
              brand ||
              search ||
              attributes ||
              price_from ||
              price_to != 999999999 ? (
                <Fragment>
                  <Filter
                    category={category}
                    categories={categories}
                    brand={brand}
                    search={search}
                    price_from={price_from}
                    price_to={price_to}
                    attributes={
                      query.attributes ? query.attributes.split(",") : []
                    }
                  />
                  <Products
                    sm={6}
                    lg={4}
                    xl={3}
                    category={category}
                    categories={categories}
                    brand={brand}
                    search={search}
                    price_from={price_from}
                    price_to={price_to}
                    attributes={attributes}
                  />
                </Fragment>
              ) : (
                <Fragment>
                  <div style={css.filter}>
                    <h6
                      style={{
                        ...css.h6,
                        display: "inline-block",
                        marginRight: theme.spacing(2),
                      }}
                    >
                      Sản Phẩm:{" "}
                    </h6>
                    Khuyến Mãi
                  </div>

                  <Products first={4} lg={3} xl={3} sale more={false} />
                  <div style={css.filter}>
                    <h6
                      style={{
                        ...css.h6,
                        display: "inline-block",
                        marginRight: theme.spacing(2),
                      }}
                    >
                      Sản Phẩm:{" "}
                    </h6>
                    Bán Chạy
                  </div>

                  <Products
                    first={4}
                    lg={3}
                    xl={3}
                    suggestions="bestSeller"
                    more={false}
                  />

                  <div style={css.filter}>
                    <h6
                      style={{
                        ...css.h6,
                        display: "inline-block",
                        marginRight: theme.spacing(2),
                      }}
                    >
                      Sản Phẩm:{" "}
                    </h6>
                    Mới Về
                  </div>

                  <Products
                    first={4}
                    lg={3}
                    xl={3}
                    suggestions="new"
                    more={false}
                  />
                </Fragment>
              )}
            </div>
          </Col>
        </Row>
      </Container>
    </Fragment>
  );
};
export default Index;
