import makeStyles from "@material-ui/core/styles/makeStyles";
import {
  Paper,
  Stepper,
  StepLabel,
  Typography,
  Link,
  Step,
} from "@material-ui/core";
import Customer from "./Customer";
import OrderItem from "./orderItem";
import formatMoney from "../lib/formatMoney";
const steps = ["Đặt", "Xử lí", "Giao", "Thanh toán"];
const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
    marginBottom: theme.spacing(2),
  },
}));
export default function Order({ order }) {
  const classes = useStyles();

  const [activeStep, setActiveStep] = React.useState(0);
  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };
  console.log(order);
  return (
    <Paper className={classes.root}>
      <Stepper activeStep={order?.step} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <Typography variant="body1">Đơn:</Typography>
      <Typography variant="body2" color="textSecondary">
        Time:{" "}
        <Link color="primary">
          {new Date(order?.time).toLocaleDateString()}
        </Link>
      </Typography>
      <Typography variant="body2" color="textSecondary">
        Id: <Link color="primary">{order?.id}</Link>
      </Typography>
      <Typography variant="body2" color="textSecondary">
        Tổng: <Link color="primary">{formatMoney(order?.total, 0)}</Link>
      </Typography>
      <Customer customer={order?.customer} />
      <OrderItem items={order?.items} />
    </Paper>
  );
}
