import { useEffect } from "react";
import { useRouter } from "next/router";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import { useState } from "react";
import { Button } from "@material-ui/core";
import Navbar from "../components/Navbar";
import CreateKetQua from "../components/createKetQua";
import CreateLaSo from "../components/createLaSo";
import LaSoTuVi from "../components/LaSoTuVi";
import KetQuas from "../components/ketQuas";

const Index = () => {
  const router = useRouter();
  const [laSoTuVi, setLaSoTuVi] = useState(false);
  const [newKetQua, setNewKetQua] = useState();

  return (
    <Container maxWidth="lg">
      <Navbar />
      <Grid container spacing={1}>
        <Grid item xs={12} md={4} lg={5}>
          <CreateLaSo onCreate={setLaSoTuVi} />
          <CreateKetQua laSoTuVi={laSoTuVi} onCreate={setNewKetQua} />
        </Grid>

        <Grid item xs={12} md={8} lg={7}>
          <LaSoTuVi data={laSoTuVi} />
        </Grid>

        <Grid item xs={12}>
          <Button>Reload</Button>
          <KetQuas newKetQua={newKetQua} />
        </Grid>
      </Grid>
    </Container>
  );
};
export default Index;
