Index.getInitialProps = async ({
  res,
  client
}) => {
  //get variables from server
  const query = `query {
    allCungs {
      id
      name
      tieuDe1
      tieuDe2
      tieuDe3
      tieuDe4
      tieuDe5
    }
    allCungVis {
      id
      name
    }
    allTinhs {
      id
      name
    }
    allPhuTinhs {
      id
      name
    }
    allKetQuas (orderBy: "time_DESC") {
      id
      cung {
        name
      }
      ketQua {
        id
        cung {
          name
        }
        chinhTinh {
          id
          name
        }
        phuTinh {
          id
          name
        }
        giai
      }
    }
  }`;

  try {
    const data = await client.request(query);
    return data;
  } catch (error) {
    cookie.remove("token");

    if (res) {
      res.writeHead(302, {
        Location: "/login"
      });
      res.end();
    }
  }
};

