import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import LabelIcon from "@material-ui/icons/Label";
import StarIcon from "@material-ui/icons/Star";
import StarBorderIcon from "@material-ui/icons/StarBorder";
import HighlightOffIcon from "@material-ui/icons/HighlightOff";
import { makeStyles } from "@material-ui/core/styles";
import { useState, useEffect } from "react";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(1),
    marginBottom: theme.spacing(2),
  },
  box: {
    backgroundColor: "transparent",
    border: 1,
    borderColor: "rgba(0, 0, 0, 0.3)",
    borderStyle: "solid",
    borderRadius: 8,
    padding: theme.spacing(1),
    marginBottom: theme.spacing(2),
  },
  boxNew: {
    backgroundColor: "rgba(255, 255, 255, 0.15)",
    border: 1,
    borderColor: "rgba(0, 0, 0, 0.3)",
    borderStyle: "solid",
    borderRadius: 8,
    padding: theme.spacing(1),
  },
  cung: {
    fontWeight: "bold",
    color: "#dab857",
  },
}));
export default function loiGiais({ ketQua, newKetQua }) {
  const classes = useStyles();
  return (
    <Grid key={ketQua.id} item xs={12}>
      <Box
        className={newKetQua?.id == ketQua.id ? classes.boxNew : classes.box}
      >
        <List>
          {/* {newKetQua ? (
            <ListItem>
              <ListItemIcon>
                <HighlightOffIcon />
              </ListItemIcon>
            </ListItem>
          ) : null} */}
          <ListItem>
            <ListItemIcon>
              <LabelIcon />
            </ListItemIcon>
            <ListItemText
              primary={
                <Typography
                  className={classes.cung}
                  //  color="primary"
                  variant="h6"
                >
                  {`Cung ${ketQua?.cung.name}`}
                </Typography>
              }
            />
          </ListItem>
        </List>
        {ketQua.loiGiais.map((loiGiai) => {
          let chinhTinhs = loiGiai.chinhTinh
            .map((tinh) => tinh.name)
            .toString();
          let phuTinhs = loiGiai.phuTinh.map((tinh) => tinh.name).toString();

          return (
            <List component="nav" key={loiGiai.id}>
              {chinhTinhs && phuTinhs ? (
                <ListItem>
                  <ListItemIcon>
                    <StarIcon />
                  </ListItemIcon>
                  <ListItemText
                    primary={`Chính tinh ${chinhTinhs}. Phụ Tinh ${phuTinhs}`}
                    secondary={loiGiai.giai}
                  />
                </ListItem>
              ) : chinhTinhs ? (
                <ListItem>
                  <ListItemIcon>
                    <StarIcon />
                  </ListItemIcon>
                  <ListItemText
                    primary={`Chính tinh ${chinhTinhs}`}
                    secondary={loiGiai.giai}
                  />
                </ListItem>
              ) : phuTinhs ? (
                <ListItem>
                  <ListItemIcon>
                    <StarBorderIcon />
                  </ListItemIcon>
                  <ListItemText
                    primary={`Phụ tinh ${phuTinhs}. `}
                    secondary={loiGiai.giai}
                  />
                </ListItem>
              ) : null}
            </List>
          );
        })}
      </Box>
    </Grid>
  );
}
