import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import LabelIcon from "@material-ui/icons/Label";
import StarIcon from "@material-ui/icons/Star";
import StarBorderIcon from "@material-ui/icons/StarBorder";
import HighlightOffIcon from "@material-ui/icons/HighlightOff";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import Skeleton from "@material-ui/lab/Skeleton";

import { useState, useEffect } from "react";
import { gql, useQuery } from "@apollo/client";

const query = gql`
  query {
    allKetQuas(orderBy: "time_DESC", first: 12) {
      id
      cung {
        name
      }
      loiGiais: ketQua {
        id
        cung {
          name
        }
        chinhTinh {
          id
          name
        }
        phuTinh {
          id
          name
        }
        giai
      }
    }
  }
`;
const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(1),
    marginBottom: theme.spacing(2),
  },
  box: {
    backgroundColor: "transparent",
    border: 1,
    borderColor: "rgba(0, 0, 0, 0.3)",
    borderStyle: "solid",
    borderRadius: 8,
    padding: 16,
  },
  boxNew: {
    backgroundColor: "transparent",
    border: 1,
    borderColor: "green",
    borderStyle: "solid",
    borderRadius: 8,
    padding: 16,
  },
  cung: {
    fontWeight: "bold",
    color: "#dab857",
  },
}));
export default function ketQuas({ newKetQua }) {
  const classes = useStyles();
  const { data, loading, error } = useQuery(query);
  if (data?.allKetQuas && newKetQua) {
    data?.allKetQuas.concat(newKetQua);
  }
  return (data?.allKetQuas && !loading) || newKetQua ? (
    <Paper className={classes.root}>
      <Grid container spacing={1}>
        {data.allKetQuas.map((ketQua) => {
          return (
            <Grid key={ketQua.id} item xs={12}>
              <Box
                className={
                  ketQua.id == newKetQua?.id ? classes.boxNew : classes.box
                }
              >
                <List>
                  {/* <ListItem>
                    <ListItemIcon>
                      <HighlightOffIcon />
                    </ListItemIcon>
                  </ListItem> */}
                  <ListItem>
                    <ListItemIcon>
                      <LabelIcon />
                    </ListItemIcon>
                    <ListItemText
                      primary={
                        <Typography
                          className={classes.cung}
                          //  color="primary"
                          variant="h6"
                        >
                          {`Cung ${ketQua.cung.name}`}
                        </Typography>
                      }
                    />
                  </ListItem>
                </List>
                {ketQua.loiGiais.map((loiGiai) => {
                  let chinhTinhs = loiGiai.chinhTinh
                    .map((tinh) => tinh.name)
                    .toString();
                  let phuTinhs = loiGiai.phuTinh
                    .map((tinh) => tinh.name)
                    .toString();

                  return (
                    <List component="nav" key={loiGiai.id}>
                      {chinhTinhs && phuTinhs ? (
                        <ListItem>
                          <ListItemIcon>
                            <StarIcon />
                          </ListItemIcon>
                          <ListItemText
                            primary={`Chính tinh ${chinhTinhs}. Phụ Tinh ${phuTinhs}`}
                            secondary={loiGiai.giai}
                          />
                        </ListItem>
                      ) : chinhTinhs ? (
                        <ListItem>
                          <ListItemIcon>
                            <StarIcon />
                          </ListItemIcon>
                          <ListItemText
                            primary={`Chính tinh ${chinhTinhs}`}
                            secondary={loiGiai.giai}
                          />
                        </ListItem>
                      ) : phuTinhs ? (
                        <ListItem>
                          <ListItemIcon>
                            <StarBorderIcon />
                          </ListItemIcon>
                          <ListItemText
                            primary={`Phụ tinh ${phuTinhs}. `}
                            secondary={loiGiai.giai}
                          />
                        </ListItem>
                      ) : null}
                    </List>
                  );
                })}
              </Box>
            </Grid>
          );
        })}
      </Grid>
    </Paper>
  ) : (
    <Skeleton animation="wave" variant="rect" height="60vh" />
  );
}
