import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Cung from "./cung";
import CungVi from "./cungVi";
import AllChinhTinhs from "./chinhTinhs";
import AllPhuTinhs from "./phuTinhs";
import { useState, useEffect } from "react";
import { layCung } from "../lib/laSoAdapter";
const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(1),
    marginBottom: theme.spacing(2),
  },
}));
const createKetQuas = ({ laSoTuVi, data, onSubmit }) => {
  const [autocomplete, setAuto] = useState(false);

  //
  const [cung, setCung] = useState({});
  const [cungVi, setCungVi] = useState({});
  const [chinhTinhs, setChinhTinhs] = useState([]);
  const [phuTinhs, setPhuTinhs] = useState([]);
  // let variables = {
  //   cung: cung?.id,
  //   cungVi: cungVi?.id,
  //   chinhTinhs: onlyId(chinhTinhs),
  //   phuTinhs: onlyId(phuTinhs),
  // };
  // auto fill
  const [autoFill, setAutoFill] = useState(false);
  // autocomplete = true
  const [DX, setDX] = useState();
  const [T1, setT1] = useState();
  const [T2, setT2] = useState();
  const [XC, setXC] = useState();
  // // autocomplete = false
  const [CV, setCV] = useState();
  const [CT, setCT] = useState([]);
  const [PT, setPT] = useState([]);
  const [PTT1, setPTT1] = useState([]);
  const [PTT2, setPTT2] = useState([]);
  const [PTXC, setPTXC] = useState([]);

  useEffect(() => {});
  function handleCungChange(cung) {
    setCung(cung);

    if (cung && laSoTuVi && autocomplete) {
      let { DX, T1, T2, XC } = layCung({
        cung,
        thapNhiCung: laSoTuVi.thapNhiCung,
      });
      console.log(DX);
      setDX(DX);
      setT1(T1);
      setT2(T2);
      setXC(XC);
    }
  }
  function handleChangeCheckBox(event) {
    setAuto(event.target.checked);
  }
  const classes = useStyles();
  return (
    <Paper className={classes.root}>
      <Grid container spacing={1}>
        <Grid item xs={12}>
          <Cung onChange={handleCungChange} />
        </Grid>
        <Grid item xs={12}>
          <CungVi
            cung={autocomplete ? DX : false}
            setCung={setDX}
            CV={CV}
            setCV={setCV}
          />
        </Grid>
        <Grid item xs={12}>
          <AllChinhTinhs
            cung={autocomplete ? DX : false}
            setCung={setDX}
            CT={CT}
            setCT={setCT}
          />
        </Grid>
        <Grid item xs={12}>
          <AllPhuTinhs cung={DX} setCung={setDX} PT={PT} setPT={setPT} />
        </Grid>
        <Grid item xs={12}>
          <AllPhuTinhs
            label={T1 ? "Tam hợp " + T1.cungChu : false}
            cung={autocomplete ? T1 : false}
            setCung={setT1}
            PT={PTT1}
            setPT={setPTT1}
          />
        </Grid>
        <Grid item xs={12}>
          <AllPhuTinhs
            label={T2 ? "Tam hợp " + T2.cungChu : false}
            cung={autocomplete ? T2 : false}
            setCung={setT2}
            PT={PTT2}
            setPT={setPTT2}
          />
        </Grid>
        <Grid item xs={12}>
          <AllPhuTinhs
            label={XC ? "Xung chiếu " + XC.cungChu : false}
            cung={autocomplete ? XC : false}
            setCung={setXC}
            PT={PTXC}
            setPT={setPTXC}
          />
        </Grid>
        <Grid item xs={12}>
          <FormControlLabel
            control={
              <Checkbox
                checked={autocomplete}
                onChange={handleChangeCheckBox}
                name="checkedB"
                color="primary"
              />
            }
            label="Tự động điền từ lá sô?"
          />
        </Grid>
        <Grid item xs={12}>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={() =>
              onSubmit({
                cung: cung?.is,
                cungVi: cungVi?.id,
                chinhTinhs: onlyId(chinhTinhs),
                phuTinhs: onlyId(phuTinhs),
              })
            }
          >
            Xem Kết Quả
          </Button>
        </Grid>
      </Grid>
    </Paper>
  );
};
export default createKetQuas;
function onlyId(array) {
  if (!array) return [];
  let result = [];
  array.map((item) => result.push(item.id));
  return result;
}
