import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";
import Skeleton from "@material-ui/lab/Skeleton";
import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { gql, useQuery } from "@apollo/client";
import { layChinhTinhs } from "../lib/laSoAdapter";
const query = gql`
  query {
    allChinhTinhs: allTinhs {
      id
      name
    }
  }
`;
export default function chinhTinhs({ cung, CT, setCT }) {
  const { data, loading, error } = useQuery(query);
  const shouldRedirect = !(loading || data);
  const router = useRouter();
  useEffect(() => {
    if (shouldRedirect) {
      router.push("/signin");
    }
  }, [shouldRedirect]);
  if (CT.length == 0 && data?.allChinhTinhs && cung) {
    let ketQua = layChinhTinhs({ allChinhTinhs: data.allChinhTinhs, cung }).CT;
    if (ketQua.length == 0) ketQua.push("trong");
    setCT(ketQua);
  }

  return data?.allChinhTinhs && !loading ? (
    <Autocomplete
      value={CT ? (CT[0] == "trong" ? "" : CT) : ""}
      onChange={(event, value) => {
        if (value) setCT(value);
      }}
      multiple
      size="small"
      options={data.allChinhTinhs}
      getOptionLabel={(option) => {
        if (option) return option.name;
      }}
