import { useRouter } from "next/router";
import { gql, useQuery } from "@apollo/client";
import { layChinhTinhs } from "../lib/laSoAdapter";
const query = gql`
  query {
    allChinhTinhs: allTinhs {
      id
      name
    }
  }
`;
