import { useEffect } from "react";
import { useRouter } from "next/router";
import { gql, useQuery } from "@apollo/client";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import { useState } from "react";

import Navbar from "../components/Navbar";
import CreateKetQua from "../components/createKetQua";
import CreateLaSo from "../components/createLaSo";
import LaSoTuVi from "../components/LaSoTuVi";
import KetQuas from "../components/ketQuas";

const Index = () => {
  const router = useRouter();
  const [laSoTuVi, setLaSoTuVi] = useState(false);

  async function onClickCreateLaSo({ ngaySinh, gioSinh, amLich, gioiTinh }) {
    setLaSoTuVi(true);
    const res = await fetch(
      `/laso?ngaySinh=${ngaySinh}&gioSinh=${gioSinh}&amLich=${amLich}&gioiTinh=${gioiTinh}`
    );
    const data = await res.json();
    setLaSoTuVi(data);
  }

  function onSubmitCreateKetQua({ cung, cungVi, chinhTinhs, phuTinhs }) {
    console.log(cung, cungVi, chinhTinhs, phuTinhs);
  }
  return (
    <Container maxWidth="lg">
      <Navbar />
      <Grid container spacing={1}>
        <Grid item xs={12} md={4} lg={5}>
          <CreateLaSo onCreate={setLaSoTuVi} />
          <CreateKetQua laSoTuVi={laSoTuVi} onSubmit={onSubmitCreateKetQua} />
        </Grid>

        <Grid item xs={12} md={8} lg={7}>
          <LaSoTuVi data={laSoTuVi} />
        </Grid>

        <Grid item xs={12}>
          <KetQuas />
        </Grid>
      </Grid>
    </Container>
  );
};
export default Index;
