import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";
import MenuItem from "@material-ui/core/MenuItem";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Skeleton from "@material-ui/lab/Skeleton";
import { Grow } from "@material-ui/core";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import Button from "@material-ui/core/Button";
import { useState } from "react";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(1),
    marginBottom: theme.spacing(2),
  },
}));
const gioSinhs = [
  {
    value: "1",
    label: "Tí (23g - 1g)",
  },
  {
    value: "2",
    label: "Sửu (1g - 3g)",
  },
  {
    value: "3",
    label: "Dần (3g - 5g)",
  },
  {
    value: "4",
    label: "Mão (5g - 7g)",
  },
  {
    value: "5",
    label: "Thìn (7g - 9g)",
  },
  {
    value: "6",
    label: "Tị (9g - 11g)",
  },
  {
    value: "7",
    label: "Ngọ (11g - 13g)",
  },
  {
    value: "8",
    label: "Mùi (13g - 15g)",
  },
  {
    value: "9",
    label: "Thân (15g - 17g)",
  },
  {
    value: "10",
    label: "Dậu (17g - 19g)",
  },
  {
    value: "11",
    label: "Tuất (19g - 21g)",
  },
  {
    value: "12",
    label: "Hợi (21g - 23g)",
  },
];
const createLaSo = ({ onSubmit }) => {
  const classes = useStyles();
  const [ngaySinh, setNgaySinh] = useState(
    new Date().toLocaleDateString("fr-CA").replace(/\//g, "-")
  );
  const [gioSinh, setGioSinh] = useState(1);
  const [amLich, setAmLich] = useState("off");
  const [gioiTinh, setGioiTinh] = useState("nam");
  const handleChange = (event) => {
    setGioSinh(event.target.value);
  };
  const handleDateChange = (date) => {
    setNgaySinh(date.target.value);
  };
  const handleAmLichChange = (event) => {
    setAmLich(event.target.value);
  };
  const handleGioiTinhChange = (event) => {
    setGioiTinh(event.target.value);
  };
  const handleSubmit = () => {
    onSubmit({
      ngaySinh,
      gioSinh,
      amLich,
      gioiTinh,
    });
    console.log(ngaySinh, gioSinh, amLich, gioiTinh);
  };
  return (
    <Paper className={classes.root}>
      <Grid container spacing={1}>
        <Grid item xs={12}>
          <TextField
            id="date"
            size="small"
            fullWidth
            label="Ngày sinh"
            type="date"
            InputLabelProps={{
              shrink: true,
            }}
            variant="outlined"
            onChange={handleDateChange}
            defaultValue={ngaySinh}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            id="outlined-select-gioSinh"
            size="small"
            fullWidth
            select
            label="Giờ sinh"
            value={gioSinh}
            onChange={handleChange}
            variant="outlined"
          >
            {gioSinhs.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>
        </Grid>
        <Grid item xs={6}>
          <FormControl component="fieldset">
            <FormLabel component="legend">Lấy theo</FormLabel>
            <RadioGroup onChange={handleAmLichChange}>
              <FormControlLabel
                value={"off"}
                checked={amLich == "off"}
                control={<Radio />}
                label="Dương lịch"
              />
              <FormControlLabel
                checked={amLich == "on"}
                value="on"
                control={<Radio />}
                label="Âm lịch"
              />
            </RadioGroup>
          </FormControl>
        </Grid>
        <Grid item xs={6}>
          <FormControl component="fieldset">
            <FormLabel component="legend">Giới tính</FormLabel>
            <RadioGroup onChange={handleGioiTinhChange}>
              <FormControlLabel
                value="nam"
                checked={gioiTinh == "nam"}
                control={<Radio />}
                label="Nam"
              />
              <FormControlLabel value="nu" control={<Radio />} label="Nữ" />
            </RadioGroup>
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={handleSubmit}
          >
            Lấy Lá Số
          </Button>
        </Grid>
      </Grid>
    </Paper>
  );
};
export default createLaSo;
