import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Snackbar from "@material-ui/core/Snackbar";
import EditIcon from "@material-ui/icons/Edit";
import CircularProgress from "@material-ui/core/CircularProgress";
import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { gql, useMutation } from "@apollo/client";

import { layCung } from "../lib/laSoAdapter";
import { getErrorMessage } from "../lib/form";
import {
  TextField,
  InputAdornment,
  Typography,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Link,
  Fab,
  Drawer,
} from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";
import Customer from "./Customer";
import OrderItem from "./orderItem";
import formatMoney from "../lib/formatMoney";
const createKetQuaMutation = gql`
  mutation(
    $cung: CungWhereUniqueInput
    $cungVi: CungViWhereUniqueInput
    $chinhTinhs: [TinhWhereUniqueInput]
    $phuTinhs: [PhuTinhWhereUniqueInput]
  ) {
    createKetQua(
      data: {
        cung: { connect: $cung }
        cungVi: { connect: $cungVi }
        chinhTinh: { connect: $chinhTinhs, disconnectAll: true }
        phuTinh: { connect: $phuTinhs, disconnectAll: true }
      }
    ) {
      id
      cung {
        name
      }
      loiGiais: ketQua {
        id
        cung {
          name
        }
        chinhTinh {
          id
          name
        }
        phuTinh {
          id
          name
        }
        giai
      }
    }
  }
`;
const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
    marginBottom: theme.spacing(2),
    height: "70vh",
  },
  icon: { marginRight: theme.spacing(1) },
}));
export default function createKetQuas({ onCreate }) {
  const [allProducts, setAllProducts] = useState([]);
  const [drawerStatus, setDrawerStatus] = useState(false);
  const [order, setOrder] = useState();
  const [createKetQua] = useMutation(createKetQuaMutation);
  const router = useRouter();

  //
  const [loading, setLoading] = useState(false);
  // alert
  const [alertOpen, setAlertOpen] = React.useState(false);
  const [alertMessage, setAlertMessage] = React.useState("");

  useEffect(() => {});

  async function handleSubmit() {
    setLoading(true);
    const variables = {};
    try {
      const { data } = await createKetQua({
        variables,
      });
      onCreate(data?.createKetQua);
      setLoading(false);
    } catch (error) {
      setAlertMessage(getErrorMessage(error));
      setAlertOpen(true);
      setLoading(false);
      await router.push("/");
    }
  }
  function alertClose(event, reason) {
    if (reason === "clickaway") {
      return;
    }
    setAlertOpen(false);
  }
  const classes = useStyles();
  function handleCreate() {
    setDrawerStatus(true);
  }
  function toggleDrawer(event) {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }
    setDrawerStatus(!drawerStatus);
  }
  return (
    <Paper className={classes.root}>
      <Typography variant="body1">Đơn:</Typography>
      <Typography variant="body2" color="textSecondary">
        Id: <Link color="primary">{order?.id}</Link>
      </Typography>
      <Typography variant="body2" color="textSecondary">
        Tổng: <Link color="primary">{formatMoney(order?.total, 0)}</Link>
      </Typography>
      <Customer customer={order?.customer} />
      <TableContainer>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>
                <Fab color="primary" aria-label="add" size="small">
                  <AddIcon />
                </Fab>
              </TableCell>
              <TableCell>Tên</TableCell>
              <TableCell align="right">SL</TableCell>
              <TableCell align="right">Bán</TableCell>
              <TableCell align="right">Niêm yết</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {items?.length
              ? items.map((item) => (
                  <TableRow key={item.id}>
                    <TableCell component="th" scope="row">
                      <img
                        style={{ maxWidth: "100%", maxHeight: 40 }}
                        src={
                          item.product.image
                            ? item.product.image.publicUrl
                            : item.product.images[0].file.publicUrl
                        }
                      />
                    </TableCell>
                    <TableCell>{item.product.name}</TableCell>
                    <TableCell align="right">{item.quantity}</TableCell>
                    <TableCell align="right">
                      {formatMoney(item.price, 0)}
                    </TableCell>
                    <TableCell align="right" color="textSecondary">
                      <Typography variant="body2" color="textSecondary">
                        {formatMoney(item.product.price, 0)}
                      </Typography>
                    </TableCell>
                  </TableRow>
                ))
              : null}
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
}
