import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";
import Skeleton from "@material-ui/lab/Skeleton";
import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { gql, useQuery } from "@apollo/client";
import { layCungVi } from "../lib/laSoAdapter";
const query = gql`
  query {
    allCungVis {
      id
      name
    }
  }
`;
export default function cungVi({ cung, autocomplete, onChange }) {
  const { data, loading, error } = useQuery(query);

  const shouldRedirect = !(loading || data);
  const router = useRouter();
  useEffect(() => {
    if (shouldRedirect) {
      router.push("/signin");
    }
  }, [shouldRedirect]);
  let CV;
  if (data?.allCungVis && cung && autocomplete) {
    CV = layCungVi({ cung, allCungVis: data.allCungVis }).CV;
  }
  const [cungVi, setCungVi] = useState();
  return data?.allCungVis && !loading ? (
    <Autocomplete
      value={CV || cungVi}
      onChange={(event, value) => {
        onChange(value);
        setCungVi(value);
      }}
