import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import Skeleton from "@material-ui/lab/Skeleton";

import { useState, useEffect } from "react";
import { gql, useQuery } from "@apollo/client";

import LoiGiais from "./loiGiais";
const query = gql`
  query {
    allKetQuas(orderBy: "time_DESC", first: 12) {
      id
      cung {
        id
        name
      }
      loiGiais: ketQua {
        id
        cung {
          name
        }
        chinhTinh {
          id
          name
        }
        phuTinh {
          id
          name
        }
        giai
      }
    }
  }
`;
const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(1),
    marginBottom: theme.spacing(2),
  },
  box: {
    backgroundColor: "transparent",
    border: 1,
    borderColor: "rgba(0, 0, 0, 0.3)",
    borderStyle: "solid",
    borderRadius: 8,
    padding: 16,
  },
  boxNew: {
    backgroundColor: "transparent",
    border: 1,
    borderColor: "green",
    borderStyle: "solid",
    borderRadius: 8,
    padding: 16,
  },
  cung: {
    fontWeight: "bold",
    color: "#dab857",
  },
}));
export default function ketQuas({ newKetQua }) {
  const classes = useStyles();
  let { data, loading, error } = useQuery(query);
  return data?.allKetQuas && !loading ? (
    <Paper className={classes.root}>
      <Grid container spacing={1}>
        {newKetQua
          ? data.allKetQuas
              .concat([newKetQua])
              .map((ketQua) => <LoiGiais ketQua={ketQua} />)
          : data.allKetQuas.map((ketQua) => <LoiGiais ketQua={ketQua} />)}
      </Grid>
    </Paper>
  ) : (
    <Skeleton animation="wave" variant="rect" height="60vh" />
  );
}
