import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Layout from "../src/Layout";
import LightBoard from "../src/LightBoard";
import { login } from "../utils/auth";
import Link from "../src/Link";
import Logo from "../src/logo";
import Typography from "@material-ui/core/Typography";
import React, { Component } from "react";
import { FacebookProvider, Comments, CustomChat } from "react-facebook";
import { request } from "graphql-request";
import getGraphUri from "../utils/getGraphUri";

function Login() {
  let [username, setUserName] = useState(false);
  let [password, setPassword] = useState(false);
  let [buttonStatus, setButtonStatus] = useState("Đăng nhập");
  //---------------------------------------
  async function loginButtonClick() {
    setButtonStatus("Vui lòng đợi...");
    if (username.length > 5 && password.length > 5) {
      let response = await request(`mutation($username: String, $password: String) {
      authenticateUserWithPassword(email: $username, password: $password) {
      token
      item {
          id
          email
          isAdmin
          isSupporter
          isCustomer
      }
      }}`,
           {
            username,
            password
          }

      );

      let {
        data: { authenticateUserWithPassword }
      } = response;
      if (authenticateUserWithPassword) {
        let {
          token,
          item: { id, email, isAdmin, isSupporter, isCustomer }
        } = authenticateUserWithPassword;
        setButtonStatus("Đang điều hướng...!");
        login({ token, id, email, isAdmin, isSupporter, isCustomer });
      } else {
        setButtonStatus("Sai tên đăng nhập hoặc mật khẩu.");
      }
    } else {
      setButtonStatus("Kiểm tra và thử lại");
    }
  }
  return (
    <Layout>
      <Container maxWidth="sm" style={{ padding: 8 }}></Container>
    </Layout>
  );
}
export default Login;
