import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import Skeleton from "@material-ui/lab/Skeleton";
import Typography from "@material-ui/core/Typography";

import { useState, useEffect } from "react";
import { gql, useQuery } from "@apollo/client";

import LoiGiais from "./loiGiais";
const query = gql`
  query {
    allKetQuas(orderBy: "time_DESC", first: 12) {
      id
      cung {
        id
        name
      }
      loiGiais: ketQua {
        id
        cung {
          name
        }
        chinhTinh {
          id
          name
        }
        phuTinh {
          id
          name
        }
        giai
      }
    }
  }
`;
const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(1),
    marginBottom: theme.spacing(2),
  },
}));
export default function ketQuas({ newKetQua }) {
  const classes = useStyles();
  let { data, loading, error } = useQuery(query);
  let allKetQuas = data?.allKetQuas;
  if (newKetQua && allKetQuas) {
    allKetQuas = [newKetQua].concat(allKetQuas);
  }

  return allKetQuas && !loading ? (
    <Paper className={classes.root}>
      <Grid container spacing={1}>
        {allKetQuas.length ? (
          allKetQuas.map((ketQua) => <LoiGiais ketQua={ketQua} />)
        ) : (
          <Typography variant="body1" gutterBottom>Danh sách kết quả trỗng.</Typography>
        )}
      </Grid>
    </Paper>
  ) : (
    <Skeleton animation="wave" variant="rect" height="60vh" />
  );
}
