import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";
import Skeleton from "@material-ui/lab/Skeleton";
import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { gql, useQuery } from "@apollo/client";
import { layPhuTinhs } from "../lib/laSoAdapter";
const query = gql`
  query {
    allPhuTinhs {
      id
      name
    }
  }
`;
export default function phuTinhs({ cung, label, PT, setPT }) {
  const { data, loading, error } = useQuery(query);

  const shouldRedirect = !(loading || data);
  const router = useRouter();
  useEffect(() => {
    if (shouldRedirect) {
      router.push("/signin");
    }
  }, [shouldRedirect]);
  if (PT.length == 0 && data?.allPhuTinhs && cung) {
    let ketQua = layPhuTinhs({ allPhuTinhs: data.allPhuTinhs, cung }).PT;
    if (ketQua.length == 0) ketQua.push("trong");
    setPT(ketQua);
  }
  return data?.allPhuTinhs && !loading ? (
    <Autocomplete
      value={PT}
      onChange={(event, value) => {
        setPT(value);
      }}
      multiple
      size="small"
      options={data.allPhuTinhs}
      getOptionLabel={(option) => {
        return option ? option.name : {};
      }}
      filterSelectedOptions
      renderInput={(params) => (
        <TextField
          {...params}
          variant="outlined"
          label={label ? label : "Phụ tinh"}
        />
      )}
    />
  ) : (
    <Skeleton variant="rect" height={40} />
  );
}
