import {
  Grid,
  Typography,
  TableCell,
  TableHead,
  TableRow,
  TableBody,
  TableContainer,
  Table,
  makeStyles,
  Paper,
} from "@material-ui/core";
import formatMoney from "../lib/formatMoney";
import { gql, useQuery } from "@apollo/client";

const query = gql`
  query($limit: Int) {
    allProducts(first: $limit) {
      id
      image {
        publicUrl
      }
      images {
        file {
          publicUrl
        }
      }
      name
      price
      sale
    }
  }
`;
const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
    marginBottom: theme.spacing(2),
  },
}));
export default function ProductItem({ products }) {
  const classes = useStyles();
  let { data, loading, error, fetchMore, refetch } = useQuery(query, {
    variables: { limit: 6 },
  });
  let allProducts = data?.allProducts;
  console.log(allProducts);
  return (
    <Paper className={classes.root}>
      <Typography>OK</Typography>
      <TableContainer>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell></TableCell>
              <TableCell>Tên</TableCell>
              <TableCell align="right">SL</TableCell>
              <TableCell align="right">Hiện tại</TableCell>
              <TableCell align="right">Sale</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {products?.length
              ? products.map((product) => (
                  <TableRow key={product.id}>
                    <TableCell component="th" scope="row">
                      <img
                        style={{ maxWidth: "100%", maxHeight: 80 }}
                        src={
                          product.image
                            ? product.image.publicUrl
                            : product.images[0].file.publicUrl
                        }
                      />
                    </TableCell>
                    <TableCell>{product.name}</TableCell>
                    <TableCell align="right">{product?.quantity}</TableCell>
                    <TableCell align="right">
                      {formatMoney(product.price, 0)}
                    </TableCell>
                    <TableCell align="right" color="textSecondary">
                      <Typography variant="body2" color="textSecondary">
                        {formatMoney(product.price, 0)}
                      </Typography>
                    </TableCell>
                  </TableRow>
                ))
              : null}
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
}
