import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Snackbar from "@material-ui/core/Snackbar";
import CircularProgress from "@material-ui/core/CircularProgress";
import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { gql, useMutation } from "@apollo/client";

import { layCung } from "../lib/laSoAdapter";
import { getErrorMessage } from "../lib/form";
import { TextField, InputAdornment, Typography, TableContainer, Table, TableHead, TableRow, TableCell, TableBody } from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";

const createKetQuaMutation = gql`
  mutation(
    $cung: CungWhereUniqueInput
    $cungVi: CungViWhereUniqueInput
    $chinhTinhs: [TinhWhereUniqueInput]
    $phuTinhs: [PhuTinhWhereUniqueInput]
  ) {
    createKetQua(
      data: {
        cung: { connect: $cung }
        cungVi: { connect: $cungVi }
        chinhTinh: { connect: $chinhTinhs, disconnectAll: true }
        phuTinh: { connect: $phuTinhs, disconnectAll: true }
      }
    ) {
      id
      cung {
        name
      }
      loiGiais: ketQua {
        id
        cung {
          name
        }
        chinhTinh {
          id
          name
        }
        phuTinh {
          id
          name
        }
        giai
      }
    }
  }
`;
const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
    marginBottom: theme.spacing(2),
  },
}));
export default function createKetQuas({ laSoTuVi, onCreate }) {
  const [allProducts, setAllProducts] = useState([]);
  const [createKetQua] = useMutation(createKetQuaMutation);
  const router = useRouter();

  //
  const [loading, setLoading] = useState(false);
  // alert
  const [alertOpen, setAlertOpen] = React.useState(false);
  const [alertMessage, setAlertMessage] = React.useState("");
  // autocomplete
  const [autocomplete, setAuto] = useState(true);
  const [autoEnable, setAutoEnable] = useState(false);
  // autocomplete = true data from "laSoTuVi"
  const [DX, setDX] = useState();
  const [T1, setT1] = useState();
  const [T2, setT2] = useState();
  const [XC, setXC] = useState();
  // // autocomplete = false data from child state
  const [C, setC] = useState();
  const [CV, setCV] = useState();
  const [CT, setCT] = useState([]);
  const [PT, setPT] = useState([]);
  const [PTT1, setPTT1] = useState([]);
  const [PTT2, setPTT2] = useState([]);
  const [PTXC, setPTXC] = useState([]);

  useEffect(() => {
    setAutoEnable(Boolean(laSoTuVi));
  });
  function handleCungChange(cung) {
    setC(cung);

    if (cung && laSoTuVi && autocomplete) {
      let { DX, T1, T2, XC } = layCung({
        cung,
        thapNhiCung: laSoTuVi.thapNhiCung,
      });
      setDX(DX);
      setT1(T1);
      setT2(T2);
      setXC(XC);
    }
  }
  function handleChangeCheckBox(event) {
    setAuto(event.target.checked);
  }
  async function handleSubmit() {
    setLoading(true);
    const variables = {
      cung: { id: C?.id },
      cungVi: { id: CV?.id },
      chinhTinhs: WhereUniqueInput(CT),
      phuTinhs: WhereUniqueInput(
        PT.concat(PTT1)
          .concat(PTT2)
          .concat(PTXC)
      ),
    };
    try {
      const { data } = await createKetQua({
        variables,
      });
      onCreate(data?.createKetQua);
      setLoading(false);
    } catch (error) {
      setAlertMessage(getErrorMessage(error));
      setAlertOpen(true);
      setLoading(false);
      await router.push("/");
    }
  }
  const alertClose = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }
    setAlertOpen(false);
  };
  const classes = useStyles();
  return (
    <Paper className={classes.root}>
      <Grid container spacing={1}>
        <Grid item xs={12}>
          <Typography variant="body1" color="textSecondary">
            Khách hàng:
          </Typography>
        </Grid>
        <Grid item xs={12}>
          <TextField
            fullWidth
            variant="outlined"
            size="small"
            label="Khách hàng"
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            fullWidth
            variant="outlined"
            size="small"
            label="Điện thoại"
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            fullWidth
            variant="outlined"
            size="small"
            label="Địa chỉ"
          />
        </Grid>
        <Grid item xs={12}>
          <Typography variant="body1" color="textSecondary">
            Danh sách sản phẩm:
          </Typography>
          <TableContainer>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Hình ảnh</TableCell>
                  <TableCell>Tên</TableCell>
                  <TableCell align="right">Tồn</TableCell>
                  <TableCell align="right">Giá</TableCell>
                  <TableCell align="right">Giảm</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {allProducts?.length
                  ? allProducts.map((product) => (
                      <TableRow key={product.id} hover onClick={() => {}}>
                        <TableCell component="th" scope="row">
                          <img
                            style={{ maxWidth: "100%", maxHeight: 80 }}
                            src={
                              product.image
                                ? product.image.publicUrl
                                : product.images[0].file.publicUrl
                            }
                          />
                        </TableCell>
                        <TableCell>{product.name}</TableCell>
                        <TableCell>{product.quantity}</TableCell>
                        <TableCell align="right">
                          {formatMoney(product.price, 0)}
                        </TableCell>
                        <TableCell align="right" color="textSecondary">
                          <Typography variant="body2" color="textSecondary">
                            - {formatMoney(product.sale, 0)} đ
                          </Typography>
                        </TableCell>
                      </TableRow>
                    ))
                  : null}
              </TableBody>
            </Table>
          </TableContainer>
        </Grid>
        <Grid item>
          <Button variant="contained" color="primary" fullWidth>
            Lưu
          </Button>
        </Grid>
      </Grid>
      <Snackbar
        anchorOrigin={{
          vertical: "bottom",
          horizontal: "left",
        }}
        open={alertOpen}
        onClose={alertClose}
        autoHideDuration={3000}
        message={alertMessage}
      />
    </Paper>
  );
}
