import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import LabelIcon from "@material-ui/icons/Label";
import StarIcon from "@material-ui/icons/Star";
import StarBorderIcon from "@material-ui/icons/StarBorder";
import HighlightOffIcon from "@material-ui/icons/HighlightOff";
import { useState } from "react";

function intersect(A = [], B = []) {
  let inter = false;
  A.forEach(a => {
    B.forEach(b => {
      if (a.id == b.id) inter = true;
    });
  });
  return inter;
}
export default ({ allKetQuas: data = [] }) => {
  let [allKetQuas, setAllKetQuas] = useState(data);
  let index = 0;
  return (
    <Grid container spacing={1}>
      {allKetQuas.map(ketQua => {
        ketQua.order = index;
        index++;
        ketQua.ketQua.sort((a, b) =>
          a.chinhTinh.length + a.phuTinh.length <
          b.chinhTinh.length + b.phuTinh.length
            ? 1
            : a.chinhTinh.length + a.phuTinh.length ==
              b.chinhTinh.length + b.phuTinh.length
            ? a.chinhTinh.length < b.chinhTinh.length
              ? 1
              : -1
            : -1
        );

        let existChinhTinhs = [];
        let existPhuTinhs = [];
        return (
          <Grid key={ketQua.id} item xs={12} style ={{overflowY: "scroll",
                maxHeight: 500}}
>
            <Box
              style={{
                backgroundColor: "transparent",
                border: 1,
                borderColor: "rgba(0, 0, 0, 0.3)",
                borderStyle: "solid",
                borderRadius: 8,
                padding: 16
              }}
            >
              <List>
                <ListItem>
                  <ListItemIcon>
                    <HighlightOffIcon
                      onClick={() => {
                        console.log(ketQua.order);
                        setAllKetQuas(
                          allKetQuas
                            .slice(0, ketQua.order)
                            .concat(allKetQuas.slice(ketQua.order + 1))
                        );
                      }}
                    />
                  </ListItemIcon>
                </ListItem>

                <ListItem>
                  <ListItemText
                    primary={
                      <Typography variant="h6">tuvilogic.com</Typography>
                    }
                  />
                </ListItem>
                <ListItem>
                  <ListItemIcon>
                    <LabelIcon />
                  </ListItemIcon>
                  <ListItemText
                    primary={
                      <Typography
                        style={{ fontWeight: "bold", color: "#dab857" }}
                      >{`Cung ${ketQua.cung.name}`}</Typography>
                    }
                  />
                </ListItem>
              </List>
              {ketQua.ketQua.map(giai => {
                let chinhTinhs = giai.chinhTinh
                  .map(tinh => tinh.name)
                  .toString();
                let phuTinhs = giai.phuTinh.map(tinh => tinh.name).toString();

                let available = false;
                if (
                  giai.chinhTinh.length &&
                  !intersect(giai.chinhTinh, existChinhTinhs)
                ) {
                  available = true;
                  existChinhTinhs = existChinhTinhs.concat(giai.chinhTinh);
                }
                if (
                  giai.phuTinh.length &&
                  !intersect(giai.phuTinh, existPhuTinhs)
                ) {
                  available = true;
                  existPhuTinhs = existPhuTinhs.concat(giai.phuTinh);
                }
                if (available) {
                  return (
                    <List component="nav" key={giai.id}>
                      {chinhTinhs ? (
                        <ListItem>
                          <ListItemIcon>
                            <StarIcon />
                          </ListItemIcon>
                          <ListItemText
                            primary={`Chính tinh ${chinhTinhs}. `}
                            secondary={giai.giai}
                          />
                        </ListItem>
                      ) : null}
                      {phuTinhs ? (
                        <ListItem>
                          <ListItemIcon>
                            <StarBorderIcon />
                          </ListItemIcon>
                          <ListItemText
                            primary={`Phụ tinh ${phuTinhs}. `}
                            secondary={giai.giai}
                          />
                        </ListItem>
                      ) : null}
                    </List>
                  );
                }
              })}
            </Box>
          </Grid>
        );
      })}
    </Grid>
  );
};
